func GetWebSiteByCompanyName(companyName string) ([]extract.Website, error) {
	brs, err := search.GetBaiduPcResultsByKeyword(companyName, 1, 50)
	if err != nil {
		return nil, err
	}
	hm := map[string]int{}
	var websites []extract.Website
	for _, br := range *brs {
		if strings.Contains(br.DisplayUrl, "百度") || strings.Contains(br.DisplayUrl, "baidu") || strings.Contains(br.SiteName, "百度") {
			continue
		}

		if !isCommWebsite(&br) {
			hostName := getHostNameFromDisplayUrl(br.DisplayUrl)
			if hostName == "" {
				_ = br.GetPCRealUrl()
				if br.RealUrl != "" {
					realUrl, err := url.Parse(br.RealUrl)
					if err != nil {
						continue
					}
					if realUrl.Hostname() != "" {
						hostName = realUrl.Hostname()
					}
				}
			}
			if _, ok := hm[hostName]; ok {
				continue
			} else {
				hm[hostName] = 1
			}
			if hostName != "" {

				ws, err := GetWebSiteByHost(hostName)
				if err != nil {
					continue
				}
				websites = append(websites, *ws)
			}
		}

	}
	return websites, err
}

func isCommWebsite(br *search.SearchResult) bool {
	displayUrl := br.DisplayUrl
	displayUrl = strings.Replace(displayUrl, "http://", "", -1)
	displayUrl = strings.Replace(displayUrl, "https://", "", -1)
	finalUrl := ""
	if displayUrl != "" {
		if !isDisplayFullDomain(displayUrl) {
			_ = br.GetPCRealUrl()
			finalUrl = br.RealUrl
		} else {
			domain := getHostNameFromDisplayUrl(displayUrl)
			if domain != "" {
				finalUrl = "http://" + domain
			}
		}
	} else {
		_ = br.GetPCRealUrl()
		finalUrl = br.RealUrl
	}
	if finalUrl == "" {
		return true
	}
	for _, d := range commDomains {
		if strings.Contains(finalUrl, d) {
			return true
		}
	}
	return false
}

func getHostNameFromDisplayUrl(displayUrl string) string {
	if isDisplayFullDomain(displayUrl) {
		displayUrl = clearDisplayUrl(displayUrl)
		displayUrl = "http://" + displayUrl
		rawUrl, _ := url.Parse(displayUrl)
		if rawUrl != nil && rawUrl.Hostname() != "" {
			return rawUrl.Hostname()
		}
	}
	return ""
}

func isDisplayFullDomain(displayUrl string) bool {
	if displayUrl == "" {
		return false
	}
	displayUrl = strings.Replace(displayUrl, "http://", "", -1)
	displayUrl = strings.Replace(displayUrl, "https://", "", -1)
	if strings.Contains(displayUrl, "...") && !strings.Contains(displayUrl, "/") {
		return false
	}
	return true
}

func clearDisplayUrl(displayUrl string) string {
	displayUrl = strings.Replace(displayUrl, "http://", "", -1)
	displayUrl = strings.Replace(displayUrl, "https://", "", -1)
	displayUrl = strings.Replace(displayUrl, "...", "", -1)
	return displayUrl
}
